insert into agg_param_table_master (column_name, description, global_default_value) values ('account_qualification_group','defines the account eligibility criteria to receive the tier assuming the PI is provisioned to the customer (not all provisioned customers will qualify and not all included customers will be provisioned to). Billed OR Generated usage for the qualifying account will be affected by the tier. Nullable (means contained to the provisioned account).','jonah');
insert into agg_param_table_master (column_name, description, global_default_value) values ('usage_qualification_group',' defines the subset of usage eligible for the tier assuming the account is eligible. Might be Nullable (means ALL usage- Should never be done.)','all');
