insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_name','Optional name of the tier for the purposes of targeted overriding. This means that if any ICB rows of a given name exist in a given schedule, all PO rows are ignored for that underlying schedule for that time period. Nullable (means not available for targeted override)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_priority','Universal tier priority allowing for a deterministic ordering across all tiers system-wide. Ties will be broken by the id_pi etc. to enforce guaranteed deterministic behavior. Nullable.',0);
