insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_metric','can be any of the following: events, parent events, units, dollars. Nullable (means units)','UNITS');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_is_counter','determines whether tier is meant to count beyond when the tier may potentially apply to usage events. If 1, then the tier will keep the counter open forever.','0');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_domain',' can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means countable)','COUNTABLE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_rate_impact',' can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means ratable)','RATABLE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_discount_impact',' can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means discountable)','DISCOUNTABLE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_charge_impact',' can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means chargeable)','CHARGEABLE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_domain_impact','This supercedes the discount/charge/rate impact fields if set. Can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means use component impacts)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_proration_method',' can be any of the following: don’t prorate, prorate on activation, prorate on termination, prorate on activation or termination. Nullable (means don’t prorate)','NO PRORATION');
insert into agg_param_table_master (column_name, description, global_default_value) values ('tier_repetition','can be any of the following: none, individual, group. Group means all tiers where tier_repetition is set to group for this id_sched for this usage_qualification_group for this tier metric will repeat as a group. Nullable (means don’t repeat)','NONE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('per_unit_rate',' if specified, usage according to the various usage target criteria (qualification_groups, tier_type) will be re-rated per unit according to this rate. Nullable (means no per unit rating will be done)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('per_event_rate','if specified, usage according to the various usage target criteria (qualification_groups, tier_type) will have the per event rate added to the calculated per_unit rate. If no per_unit rate is specified, it will simply be the per event rate applied. Nullable (means no per event rating will be done)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('charge_type','Can be proportional, inverse_proportional, or currency_amount. Nullable (means currency_amount)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('charge_on_inbound','if the charge_type is currency_amount, a charge/discount of this amount will be applied to the account when the first usage event is applied to this tier. Nullable (means no charges will be generated)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('charge_on_outbound','if the charge_type is currency_amount, a charge/discount of this amount will be applied to the account when the last usage event is applied to this tier. Nullable (means no charges will be generated)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('charge_on_final','if the charge_type is currency_amount, a charge/discount of this amount will be applied to the account when all usage has been applied to this tier and the total is still within the range of the tier. Nullable (means no charges will be generated)',NULL);
