insert into agg_param_table_master (column_name, description, global_default_value) values ('rate_currency','Currency for the per-Unit rate to allow for multiple currencies to be handled by the same PO. Null here implies that the rate will apply regardless of currency. If a currency is specified but does not match the account’s rating currency, the row will be ignored. Nullable (means any currency will match)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('target_acc_qual_group',' Defines the group of accounts any charges should apply to if different than the account_qualification_group. Should almost always be set to NULL. Nullable (means not a cross-group operation)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('target_usage_qual_group','Defines the group of events the rate should apply to if different than the usage_qualification_group. This is the method of identifying cross-product discounts. Should almost always be set to NULL. Nullable (means not a cross-group operation)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('target_charge_qual_group','Defines the specific charge the rate should apply to if different than the charge_qualification_group. Should almost always be set to NULL. Nullable (means not a cross-group operation)',NULL);
insert into agg_param_table_master (column_name, description, global_default_value) values ('target_domain','If there is a target_qualification_group, this can be any of the following 8 options: countable, ratable, discountable,  generatable, ratable and generatable, ratable and discountable, discountable and generatable, ratable discountable and generatable. Nullable (means countable)','COUNTABLE');
insert into agg_param_table_master (column_name, description, global_default_value) values ('cycle_unit_type','Can be one of the following: Interval, Daily, Monthly, Quarterly, Annually. All calendar-based cycles are relative to the subscription start date.  Nullable (means Interval)','INTERVAL');
